CREATE OR REPLACE FUNCTION obter_dt_visita_hospitalidade ( nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE

						 
dt_visita_w		timestamp;


BEGIN 
 
 
select	max(dt_evolucao) 
into STRICT	dt_visita_w 
from	evolucao_paciente a, 
		tipo_evolucao b 
where	a.ie_evolucao_clinica = b.cd_tipo_evolucao	 
and 	a.nr_atendimento		= nr_atendimento_p 
and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
and		coalesce(a.dt_inativacao::text, '') = '' 
and 	b.ie_evolucao_visita = 'S';
 
return dt_visita_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dt_visita_hospitalidade ( nr_atendimento_p bigint) FROM PUBLIC;

