CREATE OR REPLACE FUNCTION obter_duplic_cpf ( cd_pessoa_fisica_p text, nr_cpf_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
nr_cpf_exc_w		varchar(255);
ie_consiste_estab_w	varchar(255);


BEGIN
nr_cpf_exc_w := obter_param_usuario(5, 232, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, nr_cpf_exc_w);
ie_consiste_estab_w := obter_param_usuario(5, 233, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_consiste_estab_w);

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_cpf_p IS NOT NULL AND nr_cpf_p::text <> '') and
	((coalesce(nr_cpf_exc_w,'X') = 'X') or (nr_cpf_p <> nr_cpf_exc_w)) then
	begin
	if (coalesce(ie_consiste_estab_w,'N') = 'S') and (coalesce(wheb_usuario_pck.get_cd_estabelecimento,0) > 0) then
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica <> cd_pessoa_fisica_p
		and	coalesce(ie_status_usuario_event,'A') <> 'I'
		and	nr_cpf = nr_cpf_p
		and	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento  LIMIT 1;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	else
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica <> cd_pessoa_fisica_p
		and	coalesce(ie_status_usuario_event,'A') <> 'I'
		and	nr_cpf = nr_cpf_p  LIMIT 1;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return ie_retorno_w;

end;
$body$
