CREATE OR REPLACE FUNCTION obter_duplic_transplante (cd_pessoa_fisica_p text, nr_transplante_p text) RETURNS varchar AS $body$
DECLARE


qt_transplante_w	bigint;
ie_transplante_w	varchar(1) := 'N';


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_transplante_p IS NOT NULL AND nr_transplante_p::text <> '') then
	select	coalesce(count(*),0)
	into STRICT	qt_transplante_w
	from	pessoa_fisica
	where	cd_pessoa_fisica <> cd_pessoa_fisica_p
	and	nr_transplante = nr_transplante_p;

	if	qt_transplante_w > 0 then
		ie_transplante_w := 'S';
	else
		ie_transplante_w := 'N';
	end if;
end if;

return ie_transplante_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
