CREATE OR REPLACE FUNCTION obter_duracao_habito ( dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_anos_w			integer		:= 0;
qt_meses_w			bigint	:= 0;
qt_dias_w			bigint	:= 0;
ds_retorno_w		varchar(50)	:= '';
ds_anos_w			varchar(50)	:= '';
ds_meses_w			varchar(50)	:= '';
ds_dias_w			varchar(50)	:= '';
ds_dia_w			varchar(3);
dt_fim_w			timestamp;
dt_inicio_w			timestamp;


BEGIN
/*SO-2225714*/

if (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '') then
	begin
	dt_inicio_w := ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicio_p);
	dt_fim_w 	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_fim_p);

	if (coalesce(dt_fim_w::text, '') = '') then
		dt_fim_w := ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp());
	end if;

	if (dt_fim_w >= dt_inicio_w) then
		begin
		qt_meses_w 	:= trunc(months_between( ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_fim_w),ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicio_w)));
		qt_dias_w	:= (PKG_DATE_UTILS.extract_field('DAY',dt_fim_w) - PKG_DATE_UTILS.extract_field('DAY',dt_inicio_w))::numeric;

		if (qt_meses_w > 11) then
			qt_anos_w	:= trunc(qt_meses_w / 12);
		end if;

		qt_meses_w	:= qt_meses_w - (qt_anos_w * 12);

		if (dt_inicio_w = dt_fim_w) then
			qt_dias_w := 1;
		end if;

		if (qt_dias_w < 0) then
		   ds_dia_w		:= PKG_DATE_UTILS.extract_field('DAY',dt_inicio_w);

			if (PKG_DATE_UTILS.extract_field('DAY',pkg_date_utils.end_of(pkg_date_utils.add_month(dt_fim_w,-1,0), 'MONTH', 0)) < ds_dia_w) then
				ds_dia_w := PKG_DATE_UTILS.extract_field('DAY',pkg_date_utils.end_of(pkg_date_utils.add_month(dt_fim_w,-1,0),'MONTH', 0));
			end if;

			ds_dia_w := ds_dia_w || '/';

			qt_dias_w		:= obter_dias_entre_datas(to_date(ds_dia_w||to_char(pkg_date_utils.add_month(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_fim_w),-1,0),'mm/yyyy'),'dd/mm/yyyy'),dt_fim_w);
		end if;

		if (qt_meses_w > 0) then
			if (qt_anos_w = 1) then
				ds_anos_w 		:= '1 ' || lower(wheb_mensagem_pck.get_texto(308338))|| ', '; -- ano
			elsif (qt_anos_w > 1) then
				ds_anos_w 	:= qt_anos_w || ' ' || wheb_mensagem_pck.get_texto(308339)|| ', '; -- anos
			end if;

		elsif (qt_dias_w > 0) then
			if (qt_anos_w = 1) then
				ds_anos_w 		:= '1 ' || lower(wheb_mensagem_pck.get_texto(337220)); -- ano e
			elsif (qt_anos_w > 1) then
				ds_anos_w 	:= qt_anos_w || ' ' || wheb_mensagem_pck.get_texto(337221); -- anos e
			end if;

		elsif (qt_anos_w = 1) then
				ds_anos_w 		:= '1 ' || lower(wheb_mensagem_pck.get_texto(308338))|| ' '; -- ano
			elsif (qt_anos_w > 1) then
				ds_anos_w 	:= qt_anos_w || ' ' || wheb_mensagem_pck.get_texto(308339)|| ' '; -- anos
			end if;
		if (qt_dias_w > 0) then
			if (qt_meses_w > 0) then
				begin
				if (qt_meses_w = 1) then
					ds_meses_w	:= '1 ' || lower(wheb_mensagem_pck.get_texto(337216)); -- mes e
				elsif (qt_meses_w > 1) then
					ds_meses_w	:= qt_meses_w || ' ' || lower(wheb_mensagem_pck.get_texto(337217)); -- meses e
				end if;
			end;
			end if;
			elsif (qt_meses_w > 0) then
				begin
				if (qt_meses_w = 1) then
					ds_meses_w	:= '1 ' || lower(wheb_mensagem_pck.get_texto(308341)); -- mes
				elsif (qt_meses_w > 1) then
					ds_meses_w	:= qt_meses_w || ' ' || lower(wheb_mensagem_pck.get_texto(308342)); -- meses
				end if;
			end;
		end if;

		if (qt_dias_w > 0) then
			begin
			if (qt_dias_w = 1) then
				ds_dias_w	:= '1 ' || lower(wheb_mensagem_pck.get_texto(307265)); -- dia
			elsif (qt_dias_w > 1) then
				ds_dias_w	:= qt_dias_w || ' ' || wheb_mensagem_pck.get_texto(308347); -- dias
				end if;
			end;
		end if;
		end;
	else
		begin
		ds_anos_w := '';
		ds_meses_w := '';
		ds_dias_w := '';
		end;
	end if;

	ds_retorno_w := ds_anos_w||ds_meses_w||ds_dias_w;
end;
end if;

return	ds_retorno_w;

end;
$body$
