CREATE OR REPLACE FUNCTION obter_dur_servico_home_care (nr_seq_servico_p bigint, cd_pessoa_fisica_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	integer;


BEGIN

select	max(qt_tempo_minuto)
into STRICT	vl_retorno_w
from	hc_paciente_servico a,
	paciente_home_care b
where	a.nr_seq_pac_home_care = b.nr_sequencia
and	b.cd_pessoa_fisica     = cd_pessoa_fisica_p
and	nr_seq_servico	     = nr_seq_servico_p;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
