CREATE OR REPLACE FUNCTION obter_ecog_atendimento ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;


BEGIN

select	ie_nivel_ecog
into STRICT	ds_retorno_w
from (	SELECT	a.dt_avaliacao,
			a.ie_nivel_ecog
		from	can_loco_regional a
		where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	a.nr_atendimento = nr_atendimento_p
		
union

		SELECT	b.dt_avaliacao,
			b.ie_nivel_ecog
		from	escala_ecog b
		where	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		and	b.nr_atendimento = nr_atendimento_p
		order by	1 desc) alias2 LIMIT 1;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ecog_atendimento ( nr_atendimento_p bigint) FROM PUBLIC;

