SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_eis_dor ( dt_inicio_p timestamp, dt_final_p timestamp, ie_opcao_p text, cd_setor_Atendimento_p bigint, nr_seq_turno_p bigint) RETURNS bigint AS $body$
DECLARE

/* 
Total de Pacientes 'T' 
Total Pacientes Com MÃ¡xima Dor	'D' 
*/
 
			 
ds_retorno_w		bigint;
nr_sequencia_w		bigint;
dt_inicio_w		timestamp;
dt_fim_w			timestamp;
hr_inicio_dia_w		varchar(10);
hr_fim_dia_w		varchar(10);
nr_seq_result_dor_w 	bigint;
ds_restrincao_w		varchar(255);
qt_intensidade_w		bigint;

	 

BEGIN 
 
if (ie_opcao_p = 'T') then 
	 
	select	min(to_char(dt_inicial,'hh24:mi:ss')) 
	into STRICT	hr_inicio_dia_w 
	from 	turno_atendimento 
	where	to_char(dt_inicial,'hh24') <>'00' 
	and 	(to_char(dt_inicial,'hh24') IS NOT NULL AND (to_char(dt_inicial,'hh24'))::text <> '');
 
	select	min(to_char(dt_final,'hh24:mi:ss')) 
	into STRICT	hr_fim_dia_w 
	from 	turno_atendimento;
	 
	if (hr_fim_dia_w > hr_inicio_dia_w) then 
	 
		select	max(to_char(dt_final,'hh24:mi:ss')) 
		into STRICT	hr_fim_dia_w 
		from 	turno_atendimento;
		 
	end if;
 
	dt_inicio_w	:= to_date(to_char(dt_inicio_p,'dd/mm/yyyy')|| ' ' || hr_inicio_dia_w,'dd/mm/yyyy hh24:mi:ss');
	dt_fim_w		:= to_date(to_char((coalesce(dt_final_p,dt_inicio_p) + 1),'dd/mm/yyyy')|| ' ' ||hr_fim_dia_w,'dd/mm/yyyy hh24:mi:ss');
 
	select	count(*) 
	into STRICT	ds_retorno_w 
	from	resumo_atendimento_paciente_v a 
	where	trunc(a.dt_entrada) <= dt_inicio_w 
	and	coalesce(a.dt_alta,dt_fim_w)	>= dt_fim_w 
	and	a.cd_setor_atendimento = coalesce(cd_setor_Atendimento_p,a.cd_setor_atendimento) 
	order by 	a.nr_atendimento;
 
elsif (ie_opcao_p = 'D') then 
 
	If (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then 
		 
		select 	max(b.qt_intensidade) 
		into STRICT	qt_intensidade_w 
		from	w_eis_escala_dor a, 
			escala_dor_regra b 
		where	a.nr_seq_result_dor = b.nr_sequencia 
		and	trunc(a.dt_sinal_vital) between trunc(dt_inicio_p) and trunc(dt_final_p) 
		and	a.cd_setor_atendimento = coalesce(cd_setor_Atendimento_p,a.cd_setor_atendimento) 
		and	a.nr_seq_turno_atend = coalesce(nr_seq_turno_p,a.nr_seq_turno_atend) 
		and	a.qt_avaliacao_dor <> 0;
 
		select	nr_sequencia 
		into STRICT	nr_seq_result_dor_w 
		from	escala_dor_regra 
		where	qt_intensidade = qt_intensidade_w;		
 
		select	count(distinct cd_setor_atendimento) 
		into STRICT	ds_retorno_w 
		from	w_eis_escala_dor 
		where	trunc(dt_sinal_vital) between(dt_inicio_p) and (dt_final_p) 
		and	nr_seq_result_dor = nr_seq_result_dor_w 
		and	cd_setor_atendimento = coalesce(cd_setor_Atendimento_p,cd_setor_atendimento) 
		and	nr_seq_turno_atend = coalesce(nr_seq_turno_p,nr_seq_turno_atend) 
		and	qt_avaliacao_dor <> 0;
 
 
	else 
 
		select 	max(b.qt_intensidade) 
		into STRICT	qt_intensidade_w 
		from	w_eis_escala_dor a, 
			escala_dor_regra b 
		where	a.nr_seq_result_dor = b.nr_sequencia 
		and	trunc(a.dt_sinal_vital) = trunc(dt_inicio_p) 
		and	a.cd_setor_atendimento = coalesce(cd_setor_Atendimento_p,a.cd_setor_atendimento) 
		and	a.nr_seq_turno_atend = coalesce(nr_seq_turno_p,a.nr_seq_turno_atend) 
		and	a.qt_avaliacao_dor <> 0;
		 
		select	nr_sequencia 
		into STRICT	nr_seq_result_dor_w 
		from	escala_dor_regra 
		where	qt_intensidade = qt_intensidade_w;
 
		select	count(distinct cd_setor_atendimento) 
		into STRICT	ds_retorno_w 
		from	w_eis_escala_dor 
		where	trunc(dt_sinal_vital) = (dt_inicio_p) 
		and	nr_seq_result_dor = nr_seq_result_dor_w 
		and	cd_setor_atendimento = coalesce(cd_setor_Atendimento_p,cd_setor_atendimento) 
		and	nr_seq_turno_atend = coalesce(nr_seq_turno_p,nr_seq_turno_atend) 
		and	qt_avaliacao_dor <> 0;															
			 
	end if;
	 
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_eis_dor ( dt_inicio_p timestamp, dt_final_p timestamp, ie_opcao_p text, cd_setor_Atendimento_p bigint, nr_seq_turno_p bigint) FROM PUBLIC;

