SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_eis_nutricional ( nr_sequencia_p bigint, nr_atendimento_p bigint, ie_opcao_p text, ie_tabela_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p

Até 24 horas da internação		h24
Até 48 horas da internação		h48
Até 72 horas da internação		h72]

ie_tabela_p
ESCALA_STRONG_KIDS			K
ESCALA_SIST_NUTRICIONAL		N

*/
qt_retorno_w		bigint := 0;
nr_sequencia_w		bigint;
dt_avaliacao_w		timestamp;
dt_internacao_w		timestamp;


BEGIN

if (nr_atendimento_p > 0) then

	Select 	obter_data_internacao(nr_atendimento_p)
	into STRICT	dt_internacao_w
	;

end if;

if ( ie_tabela_p = 'K' ) then

	Select 	max(dt_avaliacao)
	into STRICT	dt_avaliacao_w
	from	escala_strong_kids
	where	nr_sequencia = nr_sequencia_p;

elsif ( ie_tabela_p = 'N') then

	Select 	max(dt_avaliacao)
	into STRICT	dt_avaliacao_w
	from	escala_sist_nutricional
	where	nr_sequencia = nr_sequencia_p;

end if;

if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (dt_avaliacao_w IS NOT NULL AND dt_avaliacao_w::text <> '') and (dt_internacao_w IS NOT NULL AND dt_internacao_w::text <> '') then

	if (ie_opcao_p = 'h24') then

		if	((dt_avaliacao_w - dt_internacao_w) <= 1) then
			qt_retorno_w := 1;
		end if;

	elsif (ie_opcao_p = 'h48') then

		if	((dt_avaliacao_w - dt_internacao_w) <= 2) then
			qt_retorno_w := 1;
		end if;


	elsif (ie_opcao_p = 'h72') then

		if	((dt_avaliacao_w - dt_internacao_w) <= 3) then
			qt_retorno_w := 1;
		end if;

	end if;

end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
