CREATE OR REPLACE FUNCTION obter_eis_pac_dia_atend (nr_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE

 
nr_dias_internado_w	bigint	:= 1;
dt_entrada_w		timestamp;
dt_alta_w		timestamp;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then 
	select	max(dt_entrada), 
			max(dt_alta) 
	into STRICT	dt_entrada_w, 
			dt_alta_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	if (trunc(dt_entrada_w, 'month')	= trunc(dt_inicial_p, 'month')) and (trunc(dt_alta_w, 'month')		= trunc(dt_final_p, 'month')) then 
		nr_dias_internado_w	:= trunc(trunc(dt_alta_w) - trunc(dt_entrada_w)) + 1;
 
	elsif (trunc(dt_entrada_w, 'month')	= trunc(dt_inicial_p, 'month')) and 
		((trunc(dt_alta_w, 'month')		<> trunc(dt_final_p, 'month')) or (coalesce(dt_alta_w::text, '') = '')) then 
		nr_dias_internado_w	:= trunc(trunc(dt_final_p) - trunc(dt_entrada_w)) + 1;
 
	elsif (trunc(dt_entrada_w, 'month')	<> trunc(dt_inicial_p, 'month')) and (trunc(dt_alta_w, 'month')		= trunc(dt_final_p, 'month')) then 
		nr_dias_internado_w	:= trunc(trunc(dt_alta_w) - trunc(dt_inicial_p)) + 1;
 
	else	 
		nr_dias_internado_w	:= trunc(trunc(dt_final_p) - trunc(dt_inicial_p)) + 1;
	end if;
end if;		
 
RETURN	nr_dias_internado_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
