SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_eis_tipo_baixa_mat ( cd_tipo_baixa_p bigint, ie_ocorrencia_p text) RETURNS varchar AS $body$
DECLARE


/* ie ocorrencia

R  	Requisicao
P	Prescrição
D	Devolucão
*/
ds_tipo_baixa_w	varchar(50);


BEGIN

if (ie_ocorrencia_p = 'R') then
	begin
	select	ds_motivo_baixa
	into STRICT	ds_tipo_baixa_w
	from	sup_motivo_baixa_req
	where	ie_situacao = 'A'
	and	nr_sequencia = cd_tipo_baixa_p;
	exception
	when others then
		ds_tipo_baixa_w	:=	substr(obter_valor_dominio(40, to_char(cd_tipo_baixa_p)),1,40);
	end;
else
	select	coalesce(max(ds_tipo_baixa), wheb_mensagem_pck.get_texto(301935)) -- 301935 - Não informado
	into STRICT	ds_tipo_baixa_w
	from	tipo_baixa_prescricao
	where	ie_prescricao_devolucao = ie_ocorrencia_p
	and	cd_tipo_baixa = cd_tipo_baixa_p;
end if;

return ds_tipo_baixa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
