CREATE OR REPLACE FUNCTION obter_eis_triagem ( dt_inicio_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* 
Total de ocorrências				'O' 
Total de ocorrências com classificação		'C' 
*/
			 
qt_retorno_w	bigint;
nr_sequencia_w	bigint;
	

BEGIN 
 
 
if (ie_opcao_p = 'O') then 
	 
	if (coalesce(dt_final_p::text, '') = '') then 
		select 	sum(NR_PACIENTES) 
		into STRICT	qt_retorno_w	 
		from	eis_pronto_socorro_v 
		where	trunc(dt_referencia) = trunc(dt_inicio_p);
	else 
		select 	sum(NR_PACIENTES) 
		into STRICT	qt_retorno_w	 
		from	eis_pronto_socorro_v 
		where	trunc(dt_referencia) between trunc(dt_inicio_p) and trunc(dt_final_p);
 
	end if;
	 
elsif (ie_opcao_p = 'C') then 
 
	if (coalesce(dt_final_p::text, '') = '') then 
		select 	sum(a.NR_PACIENTES) 
		into STRICT	qt_retorno_w	 
		from	eis_pronto_socorro_v a 
		where	trunc(a.dt_referencia) = trunc(dt_inicio_p) 
		and		exists (SELECT 1 
						from	triagem_pronto_atend b 
						where	b.nr_Atendimento = a.nr_atendimento);
	else 
		select 	sum(a.NR_PACIENTES) 
		into STRICT	qt_retorno_w	 
		from	eis_pronto_socorro_v a 
		where	trunc(a.dt_referencia) between trunc(dt_inicio_p) and trunc(dt_final_p) 
		and		exists (SELECT 1 
						from	triagem_pronto_atend b 
						where	b.nr_Atendimento = a.nr_atendimento);
 
	end if;
 
end if;
 
return	qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
