SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_email_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



ds_email_w		varchar(255);


BEGIN

select 	max(ds_email)
into STRICT	ds_email_w
from	COMPL_PESSOA_FISICA
where 	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_tipo_complemento = '1';

if (coalesce(ds_email_w::text, '') = '') then
	select 	max(ds_email)
	into STRICT	ds_email_w
	from	COMPL_PESSOA_FISICA
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_tipo_complemento = '2';
end if;

return	ds_email_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
