CREATE OR REPLACE FUNCTION obter_email_residencial_pf (cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


ds_email_w      compl_pessoa_fisica.ds_email%type;


BEGIN
    begin
        select  ds_email
        into STRICT    ds_email_w
        from    compl_pessoa_fisica
        WHERE   cd_pessoa_fisica = cd_pessoa_fisica_p
        and     ie_tipo_complemento = '1';
        exception
            when no_data_found or too_many_rows then
                ds_email_w := null;
    end;

return  ds_email_w;

end;
$body$
LANGUAGE PLPGSQL
