SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_email_resp_sac (nr_seq_boletim_p bigint) RETURNS varchar AS $body$
DECLARE


ds_lista_email_w	varchar(4000)	:= '';
ds_email_w	varchar(255);

c01 CURSOR FOR
SELECT	distinct
	coalesce(c.ds_email,'') ds_email
from	sac_resp_bol_ocor a,
	sac_resp_usuario b,
	usuario c
where	a.nr_seq_bo		= nr_seq_boletim_p
and	b.nr_seq_resp		= a.nr_seq_responsavel
and	c.nm_usuario		= b.nm_usuario_resp
and	coalesce(b.ie_email,'S') 	= 'S'
order by	coalesce(c.ds_email,'');


BEGIN

if (nr_seq_boletim_p IS NOT NULL AND nr_seq_boletim_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		ds_email_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ds_email_w IS NOT NULL AND ds_email_w::text <> '') then
			if (coalesce(ds_lista_email_w::text, '') = '') then
				ds_lista_email_w := ds_lista_email_w || ds_email_w;
			else
				ds_lista_email_w := ds_lista_email_w || ';' || ds_email_w;
			end if;
		end if;
		end;
	end loop;
	close c01;

end if;

return ds_lista_email_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_email_resp_sac (nr_seq_boletim_p bigint) FROM PUBLIC;

