CREATE OR REPLACE FUNCTION obter_email_usuario_cobranca ( nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_email_w	varchar(255);


BEGIN

select	max(ds_email)
into STRICT	ds_email_w
from	cobrador a,
	usuario b,
	cobranca c
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	a.nr_sequencia		= c.nr_seq_cobrador
and	b.ie_situacao		= 'A'
and	c.nr_titulo		= nr_titulo_p;

return	ds_email_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_email_usuario_cobranca ( nr_titulo_p bigint) FROM PUBLIC;

