SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_email_usuario_pf ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

	ds_retorno_w varchar(4000);

BEGIN

	select	max(cpf.ds_email)
	into STRICT	ds_retorno_w
	from	compl_pessoa_fisica cpf
	where	cpf.ie_tipo_complemento in (1, 2)
	and	cpf.cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	if (coalesce(ds_retorno_w::text, '') = '') then
		begin
			select	max(u.ds_email)
			into STRICT	ds_retorno_w
			from	usuario u
			where	u.cd_pessoa_fisica = cd_pessoa_fisica_p;		
		end;
	end if;
	
	return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_email_usuario_pf ( cd_pessoa_fisica_p text) FROM PUBLIC;

