CREATE OR REPLACE FUNCTION obter_eme_nome_paciente ( nr_seq_pessoa_p bigint, cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(100);


BEGIN 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	substr(obter_nome_pessoa_fisica(cd_pessoa_fisica_p,null),1,80) 
	into STRICT	ds_retorno_w 
	;
elsif (nr_seq_pessoa_p IS NOT NULL AND nr_seq_pessoa_p::text <> '') then 
	select	substr(obter_nome_pf_pj(cd_pessoa_fisica, cd_cgc),1,80) 
	into STRICT	ds_retorno_w 
	from	eme_pf_contrato 
	where	nr_sequencia = nr_seq_pessoa_p;
end if;
	 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
