CREATE OR REPLACE FUNCTION obter_eme_nota_fiscal (nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(4000);
nr_nota_w	varchar(255);

C01 CURSOR FOR 
	SELECT 	substr(obter_dados_nota_fiscal(nr_seq_nota_fiscal, '0'),1,100) 
	from	eme_faturamento 
	where	nr_seq_contrato = nr_seq_contrato_p 
	and	(nr_seq_nota_fiscal IS NOT NULL AND nr_seq_nota_fiscal::text <> '');
			

BEGIN 
 
if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then 
	ds_retorno_w := '';
 
	open C01;
	loop 
	fetch C01 into	 
		nr_nota_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		ds_retorno_w	:= ds_retorno_w || nr_nota_w || ',';
		 
		end;
	end loop;
	close C01;
	 
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then 
		ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-1);
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
