SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_eme_origem_destino ( nr_sequencia_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* Informação IE_TIPO
D = Descrição do local
C = CGC
*/
ds_retorno_w			varchar(60);
ds_origem_destino_w		varchar(60);
cd_cgc_w			varchar(14);


BEGIN

select	ds_origem_destino,
	cd_cgc
into STRICT	ds_origem_destino_w,
	cd_cgc_w
from	eme_origem_destino
where	nr_sequencia = nr_sequencia_p;

if (ie_tipo_p = 'D') then
	ds_retorno_w	:= ds_origem_destino_w;
elsif (ie_tipo_p = 'C') then
	ds_retorno_w	:= cd_cgc_w;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
