CREATE OR REPLACE FUNCTION obter_empresa_atend ( nr_atendimento_p bigint, ie_opcao_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao		A - Atendimento		C - Cadastro  */

/* ie_retorno	C - Código			D - Descrição */

cd_empresa_w			bigint;
ds_retorno_w			varchar(100)	:= '';


BEGIN
if (ie_opcao_p	= 'A') then
	select	max(cd_empresa)
	into STRICT	cd_empresa_w
	from	atend_categoria_convenio
	where	nr_atendimento		= nr_atendimento_p;
elsif (ie_opcao_p	= 'C') then
	select	max(c.cd_empresa_refer)
	into STRICT	cd_empresa_w
	from	compl_pessoa_fisica c,
		atendimento_paciente a
	where	a.nr_atendimento	= nr_atendimento_p
	and	a.cd_pessoa_fisica	= c.cd_pessoa_fisica
	and	c.ie_tipo_complemento	= 1;
end if;
if (ie_retorno_p = 'C') then
	ds_retorno_w		:= cd_empresa_w;
else
	select nm_empresa
	into STRICT ds_retorno_w
	from empresa_referencia
	where cd_empresa	= cd_empresa_w;
	end if;
RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_empresa_atend ( nr_atendimento_p bigint, ie_opcao_p text, ie_retorno_p text) FROM PUBLIC;

