SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_encaixe_classif_transf (nr_sequencia_original_p bigint, nr_sequencia_destino_p bigint, cd_tipo_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_bloquear_w					varchar(2)	:= 'N';
nr_seq_horario_ori_w				bigint;
nr_seq_hor_esp_ori_w				bigint;
nr_se_horario_des_w				bigint;
nr_seq_hor_esp_des_w				bigint;
ds_classif_dest_agenda_w				varchar(15);
ds_classif_ori_agenda_w			varchar(15);
qt_registros_w					bigint := 0;
ie_classif_orig_transf_w		parametro_Agenda.ie_classif_orig_transf%type;
ds_classif_regra_w				varchar(15);


BEGIN
if (cd_tipo_agenda_p = 2) then

	select	max(nr_seq_horario),
			max(nr_seq_esp),
			max(nr_seq_classif_agenda)
	into STRICT	nr_seq_horario_ori_w,
			nr_seq_hor_esp_ori_w,
			ds_classif_ori_agenda_w
	from 	agenda_paciente
	where 	nr_sequencia = nr_sequencia_original_p;
	
	select	max(a.nr_seq_horario),
			max(a.nr_seq_esp),
			max(CASE WHEN ie_classif_orig_transf='S' THEN ds_classif_ori_agenda_w  ELSE nr_Seq_classif_Agenda END ),
			max(a.nr_seq_classif_agenda)
	into STRICT	nr_se_horario_des_w,
			nr_seq_hor_esp_des_w,
			ds_classif_regra_w,
			ds_classif_dest_agenda_w
	FROM agenda_paciente a, agenda b
LEFT OUTER JOIN parametro_agenda c ON (b.cd_estabelecimento = c.cd_estabelecimento)
WHERE a.nr_sequencia 			= nr_sequencia_destino_p and a.cd_agenda				= b.cd_agenda;
	
	if (nr_seq_hor_esp_des_w IS NOT NULL AND nr_seq_hor_esp_des_w::text <> '') then
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_hor_esp = nr_seq_hor_esp_des_w;
	else
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_horario = nr_se_horario_des_w;
	end if;
	
	if (qt_registros_w = 0) or (coalesce(ds_classif_regra_w::text, '') = '') or (ds_classif_ori_agenda_w = ds_classif_dest_agenda_w) then
		goto final;
	end if;

	if (nr_seq_hor_esp_des_w IS NOT NULL AND nr_seq_hor_esp_des_w::text <> '') then
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_hor_esp = nr_seq_hor_esp_des_w
		and 	nr_seq_classif_agenda = ds_classif_regra_w;
	else
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_horario = nr_se_horario_des_w
		and 	nr_seq_classif_agenda = ds_classif_regra_w;
	end if;

	if (qt_registros_w = 0) then
		ie_bloquear_w := 'S';
	end if;
	
ELSIF (cd_tipo_agenda_p = 3) then

	select	max(nr_seq_turno),
			max(nr_seq_turno_esp),
			max(ie_classif_agenda)
	into STRICT	nr_seq_horario_ori_w,
			nr_seq_hor_esp_ori_w,
			ds_classif_ori_agenda_w
	from 	agenda_consulta
	where 	nr_sequencia = nr_sequencia_original_p;
	
	select	max(nr_seq_turno),
			max(nr_seq_turno_esp),
			max(CASE WHEN ie_classif_orig_transf='S' THEN ds_classif_ori_agenda_w  ELSE ie_classif_Agenda END ),
			max(a.ie_classif_Agenda)
	into STRICT	nr_se_horario_des_w,
			nr_seq_hor_esp_des_w,
			ds_classif_Regra_w,
			ds_classif_dest_agenda_w
	FROM agenda_consulta a, agenda b
LEFT OUTER JOIN parametro_agenda c ON (b.cd_estabelecimento = c.cd_estabelecimento)
WHERE a.nr_sequencia = nr_sequencia_destino_p and a.cd_agenda				= b.cd_agenda;
	
	if (nr_seq_hor_esp_des_w IS NOT NULL AND nr_seq_hor_esp_des_w::text <> '') then
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_turno_esp = nr_seq_hor_esp_des_w;
	else
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_turno = nr_se_horario_des_w;
	end if;

	if (qt_registros_w = 0) or (coalesce(ds_classif_Regra_w::text, '') = '')	or (ds_classif_dest_agenda_w = ds_classif_ori_agenda_w) then
		goto final;
	end if;

	if (coalesce(nr_seq_hor_esp_des_w::text, '') = '') then
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_turno = nr_se_horario_des_w
		and 	nr_seq_classif_agenda = ds_classif_Regra_w;
	else
		select 	count(1)
		into STRICT	qt_registros_w
		from 	agenda_turno_classif_lib
		where 	nr_seq_turno_esp = nr_seq_hor_esp_des_w
		and 	nr_seq_classif_agenda = ds_classif_Regra_w;
	end if;

	if (qt_registros_w = 0) then
		ie_bloquear_w := 'S';
	end if;
	
ELSIF (cd_tipo_agenda_p = 5) then

	select	max(nr_seq_turno),
		max(ie_classif_agenda)
	into STRICT	nr_seq_horario_ori_w,
		ds_classif_ori_agenda_w
	from 	agenda_consulta
	where 	nr_sequencia = nr_sequencia_original_p;
	
	select	max(nr_seq_turno)
	into STRICT	nr_se_horario_des_w
	from 	agenda_consulta
	where 	nr_sequencia = nr_sequencia_destino_p;
	
	select 	count(1)
	into STRICT	qt_registros_w
	from 	agenda_turno_classif
	where 	nr_seq_turno = nr_se_horario_des_w
	and 	cd_classificacao = ds_classif_ori_agenda_w;
	
	if (qt_registros_w > 0) then
		goto final;
	end if;
	
	select 	count(1)
	into STRICT	qt_registros_w
	from 	agenda_turno_classif_lib
	where 	nr_seq_turno = nr_se_horario_des_w;

	if (qt_registros_w = 0) or (coalesce(ds_classif_Regra_w::text, '') = '')	or (ds_classif_dest_agenda_w = ds_classif_ori_agenda_w) then
		goto final;
	end if;
	
	select 	count(1)
	into STRICT	qt_registros_w
	from 	agenda_turno_classif_lib
	where 	nr_seq_turno = nr_se_horario_des_w
	and 	ie_classif_agenda = ds_classif_ori_agenda_w;

	if (qt_registros_w = 0) then
		ie_bloquear_w := 'S';
	end if;

end if;
	
<<final>>
return	ie_bloquear_w;
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
