SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_endereco_completo (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_endereco_w	end_endereco.ds_endereco_completo%type;
nm_pais_w	pais.nm_pais%type;
qt_pais_w	integer	:= 0;WITH RECURSIVE cte AS (


c01 CURSOR FOR
SELECT	ds_endereco,ie_informacao
from 	end_endereco WHERE nr_sequencia = nr_sequencia_p
  UNION ALL


c01 CURSOR FOR
SELECT	ds_endereco,ie_informacao
from 	end_endereco JOIN cte c ON (c.prior nr_seq_superior = nr_sequencia)

) SELECT * FROM cte;
;
c01_w	c01%rowtype;


BEGIN

open c01;
loop
fetch c01 into	
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(c01_w.ds_endereco,'X') <> 'X') then
		ds_endereco_w := substr(ds_endereco_w || c01_w.ds_endereco || ', ',1,4000);
	end if;
	
	if (c01_w.ie_informacao = 'PAIS') then
		qt_pais_w	:= qt_pais_w + 1;
	end if;
	
	end;
end loop;
close c01;

ds_endereco_w := substr(ds_endereco_w,1,instr(ds_endereco_w,',',-1)-1);

begin
select 	c.nm_pais
into STRICT	nm_pais_w
from	end_endereco a,
	end_catalogo b,
	pais c
where	a.nr_seq_catalogo = b.nr_sequencia
and	b.nr_seq_pais = c.nr_sequencia
and	a.nr_sequencia = nr_sequencia_p;
exception
when others then
	nm_pais_w := null;
end;

if (coalesce(nm_pais_w,'X') <> 'X') and (qt_pais_w = 0) then
	ds_endereco_w := ds_endereco_w || ', ' || nm_pais_w;
end if;

return	ds_endereco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
