CREATE OR REPLACE FUNCTION obter_end_cnpj_ind (cd_cnpj_indicacao_p text) RETURNS varchar AS $body$
DECLARE


ds_endereco_w	varchar(255);


BEGIN

select	substr(a.ds_endereco||' '||a.nr_endereco||' '||a.ds_bairro||' '||a.ds_municipio||' '||obter_dados_pf_pj(null, a.cd_cgc, 'DS_UF'),1,255)
into STRICT	ds_endereco_w
from	pessoa_juridica a
where	a.cd_cgc = cd_cnpj_indicacao_p;

return	ds_endereco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
