CREATE OR REPLACE FUNCTION obter_enfermeiro_resp_aux ( nr_atendimento_p bigint, dt_inicial_p atend_enfermagem_resp.dt_inicio_resp%type, ie_opcao_p text, nr_seq_turno_p nurse_shift_user_daily.nr_seq_master_shift%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
cd_pessoa_fisica_w	varchar(10);
nm_pessoa_w		varchar(100);
nr_seq_phs_w    atend_enfermagem_resp.nr_seq_phs%type;


BEGIN
    begin
        select	a.cd_pessoa_fisica, a.nr_seq_phs
        into STRICT	cd_pessoa_fisica_w, nr_seq_phs_w
        from	atend_enfermagem_resp a
        where	a.nr_sequencia	= (	SELECT	max(b.nr_sequencia)
                                    from	atend_enfermagem_resp b
                                    where	b.nr_atendimento	= nr_atendimento_p)
        and	    trunc(dt_inicial_p) between trunc(a.dt_inicio_resp) and coalesce(trunc(a.dt_final_resp), trunc(clock_timestamp() + interval '365 days'))
        and     ((coalesce(NR_SEQ_TURNO_P::text, '') = '' and 1 = 1)
        or ((NR_SEQ_TURNO_P IS NOT NULL AND NR_SEQ_TURNO_P::text <> '')
                 and exists (select 1
                             from nurse_shift_user x, nurse_shift_user_daily y
                             where y.nr_seq_nurse_shift = x.nr_sequencia
                             and x.cd_pessoa_fisica = a.cd_pessoa_fisica
                             and y.nr_seq_master_shift = NR_SEQ_TURNO_P)));
    exception
      when no_data_found then
        cd_pessoa_fisica_w := null;
    end;

    if (ie_opcao_p = 'C') then
        ds_retorno_w	:= cd_pessoa_fisica_w;
    elsif (ie_opcao_p = 'P') then
        ds_retorno_w	:= nr_seq_phs_w;
    else
        ds_retorno_w	:= obter_nome_pf(cd_pessoa_fisica_w);
    end if;

    return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
