CREATE OR REPLACE FUNCTION obter_enfermeiro_resp ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_Retorno_w		varchar(100);
cd_pessoa_fisica_w	varchar(10);
nm_pessoa_w		varchar(100);


BEGIN 
 
select	a.cd_pessoa_fisica 
into STRICT	cd_pessoa_fisica_w 
from	atend_enfermagem_resp a 
where	a.nr_sequencia	= (	SELECT	max(b.nr_sequencia) 
				from	atend_enfermagem_resp b 
				where	b.nr_atendimento	= nr_atendimento_p);
 
if (ie_opcao_p = 'C') then 
	ds_retorno_w	:= cd_pessoa_fisica_w;
else 
	ds_retorno_w	:= obter_nome_pf(cd_pessoa_fisica_w);
end if;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
