CREATE OR REPLACE FUNCTION obter_entrega_parcial (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_entrega_parcial_w	varchar(1);
nr_sequencia_w		bigint;
qt_prevista_entrega_w	double precision;
qt_real_entrega_w	double precision;


BEGIN
ie_entrega_parcial_w := 'N';

select	nr_Seq_entrega
into STRICT	nr_sequencia_w
from	inspecao_recebimento
where 	nr_sequencia = nr_sequencia_p;

if	nr_sequencia_w > 0 then
	begin

	select	qt_prevista_entrega,
		coalesce(qt_real_entrega,0)
	into STRICT	qt_prevista_entrega_w,
		qt_real_entrega_w
	from	ordem_compra_item_entrega
	where	nr_sequencia = nr_sequencia_w;

	if (qt_real_entrega_w > 0) and (qt_real_entrega_w < 	qt_prevista_entrega_w) then
		  ie_entrega_parcial_w := 'S';
	end if;
	end;
end if;

return	ie_entrega_parcial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
