CREATE OR REPLACE FUNCTION obter_envia_email_result_exame ( nr_seq_resultado_p bigint, nr_seq_prescr_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1) := 'N';

BEGIN

select 	CASE WHEN coalesce(d.ie_email_aprovacao,'N')='N' THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from   	exame_lab_resultado e,
	prescr_procedimento d
where  	e.nr_prescricao 	= d.nr_prescricao
and     e.nr_seq_resultado  	= nr_seq_resultado_p
and    	d.nr_sequencia 		= nr_seq_prescr_p
and    	d.nr_seq_exame 		= nr_seq_exame_p
and    	(d.ie_email_aprovacao IS NOT NULL AND d.ie_email_aprovacao::text <> '');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
