SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_envio_auto_int_opme ( cd_estabelecimento_p bigint, nr_seq_agenda_p bigint, ie_evento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
ie_pergunta_w			varchar(1);
ie_pacote_w			varchar(1);
cd_grupo_proc_w			bigint;
cd_especialidade_w		bigint;
cd_area_proced_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_origem_proced_ww		bigint;
cd_convenio_w			integer;
cd_proc_pacote_w		varchar(1);
cd_proc_pacote_ww		varchar(1);
cd_procedimento_ww		bigint;
ie_proc_pacote_w		varchar(1) := 'S';
qt_existe_sem_codigo_w		bigint;
ie_enviar_sem_codigo_w		varchar(1) := 'S';

c01 CURSOR FOR
	SELECT	ie_enviar,
		ie_pacote,
		ie_enviar_sem_codigo
	from	opme_envio_automatico
	where	coalesce(cd_grupo_proc, cd_grupo_proc_w)		= cd_grupo_proc_w
	and	coalesce(cd_area_procedimento, cd_area_proced_w)	= cd_area_proced_w
	and	coalesce(cd_especialidade, cd_especialidade_w)	= cd_especialidade_w
	and	coalesce(cd_procedimento, cd_procedimento_w) 	= cd_procedimento_w
	and 	((ie_pacote = 'S') or ((ie_pacote = 'N') and ((coalesce(cd_proc_pacote_w,'S') = 'N') or (coalesce(ie_proc_pacote_w,'S') = 'N'))))
	and	ie_evento					=  ie_evento_p
	and 	cd_estabelecimento	 			= cd_estabelecimento_p
	order by coalesce(cd_procedimento, 0),
		coalesce(cd_grupo_proc, 0),
		coalesce(cd_especialidade, 0),
		coalesce(cd_area_procedimento, 0),
		coalesce(cd_estabelecimento, 0);

c02 CURSOR FOR
	SELECT	a.cd_procedimento,
		a.ie_origem_proced
	from	procedimento b,
		agenda_paciente_proc a
	where	a.cd_procedimento = b.cd_procedimento
	and	a.ie_origem_proced = b.ie_origem_proced
	and	a.nr_sequencia = nr_seq_agenda_p
	and	coalesce(a.nr_seq_proc_interno::text, '') = ''
	
union

	SELECT	a.cd_procedimento,
		a.ie_origem_proced
	from	proc_interno b,
		agenda_paciente_proc a
	where	a.nr_seq_proc_interno = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_agenda_p
	order by 1;
		

BEGIN

select 	max(cd_procedimento),
	max(ie_origem_proced),
	max(cd_convenio)
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	cd_convenio_w
from 	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

cd_proc_pacote_w :=  obter_se_pacote_convenio(cd_procedimento_w, ie_origem_proced_w, cd_convenio_w, cd_estabelecimento_p);


select 	count(*)
into STRICT	qt_existe_sem_codigo_w
from 	agenda_pac_desc_material
where 	nr_seq_agenda = nr_seq_agenda_p
and 	ie_tipo_item = 2;

select 	max(cd_area_procedimento),
	max(cd_grupo_proc),
	max(cd_especialidade)
into STRICT	cd_area_proced_w,
	cd_grupo_proc_w,
	cd_especialidade_w
from	estrutura_procedimento_v
where	cd_procedimento = cd_procedimento_w
and	ie_origem_proced = ie_origem_proced_w;

open C02;
loop
fetch C02 into	
	cd_procedimento_ww,
	ie_origem_proced_ww;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	
	cd_proc_pacote_ww := obter_se_pacote_convenio(cd_procedimento_ww, ie_origem_proced_ww, cd_convenio_w, cd_estabelecimento_p);
	
	if (cd_proc_pacote_ww = 'N') then
		ie_proc_pacote_w := 'N';
	end if;
	
	end;
end loop;
close C02;

open C01;
loop
fetch C01 into
	ie_pergunta_w,
	ie_pacote_w,
	ie_enviar_sem_codigo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_pergunta_w		:= ie_pergunta_w;
	ie_pacote_w		:= ie_pacote_w;
	ie_enviar_sem_codigo_w	:= ie_enviar_sem_codigo_w;
	end;
end loop;
close C01;

ds_retorno_w	:= ie_pergunta_w;

if (ie_pacote_w = 'N') then
	if ( cd_proc_pacote_w = 'S') and ( ie_proc_pacote_w = 'S') then
		ds_retorno_w := '';
	end if;
end if;

if	((ds_retorno_w = 'S') or (ds_retorno_w = 'P')) and
	((coalesce(qt_existe_sem_codigo_w,0) = 0) and (ie_enviar_sem_codigo_w = 'N')) then
	ds_retorno_w := '';
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
