CREATE OR REPLACE FUNCTION obter_envio_email_contrato ( nr_contrato_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_email_destino_w		varchar(255);
ie_envia_email_w		varchar(1);


BEGIN 
 
select	coalesce(max(ie_enviar_email_oc),'N'), 
	max(CASE WHEN coalesce(ie_enviar_email_oc,'N')='S' THEN coalesce(ds_email,'')  ELSE '' END ) 
into STRICT	ie_envia_email_w, 
	ds_email_destino_w 
from	contrato_estab_adic 
where	nr_seq_contrato = nr_contrato_p 
and	cd_estab_adic = cd_estabelecimento_p;
 
if (ie_envia_email_w = 'N') then 
	select	substr(coalesce(email_contato,obter_dados_pf_pj_estab(cd_estabelecimento,cd_pessoa_contratada,cd_cgc_contratado,'M')),1,255) 
	into STRICT	ds_email_destino_w 
	from	contrato 
	where	nr_sequencia = nr_contrato_p 
	and	ie_enviar_email_oc = 'S' 
	and	cd_estabelecimento = cd_estabelecimento_p;
end if;
 
return	ds_email_destino_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
