SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_epicrise_desc ( nr_sequencia_p bigint, ie_tipo_info_p text) RETURNS varchar AS $body$
DECLARE


ds_epicrise_w	varchar(255);


BEGIN

ds_epicrise_w := '';

if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and
	nr_sequencia_p > 0) then

	if (ie_tipo_info_p = 'ME') then
		select 	max(SUBSTR(obter_desc_material(cd_material),1,254))
		into STRICT 	ds_epicrise_w
		from 	cpoe_material
		where 	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_info_p = 'RE') then
		select 	max(SUBSTR(cpoe_obter_desc_recomendacao(cd_recomendacao, dt_liberacao), 1, 254))
		into STRICT 	ds_epicrise_w
		from 	cpoe_recomendacao
		where 	nr_sequencia = nr_sequencia_p;
	end if;
end if;

return ds_epicrise_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
