CREATE OR REPLACE FUNCTION obter_episodio_atend_tela (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_episodio_w	episodio_paciente.nr_sequencia%type;
nr_episodio_w	episodio_paciente.nr_episodio%type;


BEGIN

select	max(b.nr_episodio)
into STRICT	nr_episodio_w
from	atendimento_paciente a,
	episodio_paciente b
where	b.nr_sequencia = a.nr_seq_episodio
and	a.nr_atendimento = nr_atendimento_p;

if (coalesce(nr_episodio_w::text, '') = '') then

    select	max(b.nr_sequencia)
    into STRICT	nr_seq_episodio_w
    from	atendimento_paciente a,
        episodio_paciente b
    where	b.nr_sequencia = a.nr_seq_episodio
    and	a.nr_atendimento = nr_atendimento_p;

end if;

return  coalesce(nr_episodio_w, to_char(nr_seq_episodio_w));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
