CREATE OR REPLACE FUNCTION obter_episodio_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_episodio_w               bigint;
nr_seq_episodio_ww		episodio_paciente.nr_episodio%type;


BEGIN

select  coalesce(max(nr_seq_episodio),0)
into STRICT    nr_seq_episodio_w
from    atendimento_paciente
where   nr_atendimento = nr_atendimento_p;

select  coalesce(max(nr_episodio),0)
into STRICT    nr_seq_episodio_ww
from    episodio_paciente
where   nr_sequencia = nr_seq_episodio_w;


return  nr_seq_episodio_ww;

end;
