SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_episodio_status_atual (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

/*

    P - In Progress
    C - Canceled
    F - Closed

*/
status_w                varchar(1);
dt_cancelamento_w              timestamp;
dt_fim_episodio_w              timestamp;



BEGIN

select dt_cancelamento,
       dt_fim_episodio 
into STRICT   dt_cancelamento_w, 
       dt_fim_episodio_w
from episodio_paciente 
where nr_sequencia = nr_sequencia_p;

if (coalesce(dt_cancelamento_w::text, '') = '' and coalesce(dt_fim_episodio_w::text, '') = '') then
  status_w := 'P';
elsif (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '')then
  status_w := 'C';
elsif (dt_fim_episodio_w IS NOT NULL AND dt_fim_episodio_w::text <> '')then
  status_w := 'F';
end if;


return  status_w;

end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
