CREATE OR REPLACE FUNCTION obter_equipe_medico_espec ( cd_especialidade_p bigint, cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w 		bigint;
cd_estabelecimento_w	smallint;

C01 CURSOR FOR
	SELECT MAX(a.nr_sequencia)
	from   pf_equipe a
	where  coalesce(a.ie_situacao,'A') = 'A'
	and    a.cd_especialidade = cd_especialidade_p
	and    a.cd_estabelecimento = cd_estabelecimento_w
	and    obter_se_medico_equipe(a.nr_sequencia,cd_pessoa_fisica_p) = 'S';


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (coalesce(nr_sequencia_w,0) = 0) then

	select max(a.nr_sequencia)
	into STRICT   nr_sequencia_w
	from   pf_equipe a
	where  coalesce(a.ie_situacao,'A') = 'A'
	and    a.cd_especialidade = cd_especialidade_p
	and    a.cd_estabelecimento = cd_estabelecimento_w
	and    not exists (SELECT 1 from pf_equipe_partic b
		          where	a.nr_sequencia = b.nr_seq_equipe);

end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
