CREATE OR REPLACE FUNCTION obter_equipe_medico (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_equipe_w		varchar(80);
ds_retorno_w		varchar(2000);


C01 CURSOR FOR
SELECT	 a.ds_equipe
from	 pf_equipe_partic b,
	 pf_equipe a
where	 a.nr_sequencia = b.nr_seq_equipe
and	 ((a.cd_pessoa_fisica = cd_pessoa_fisica_p) OR (b.cd_pessoa_fisica = cd_pessoa_fisica_p))
order by a.ds_equipe;


BEGIN

OPEN	C01;
LOOP
FETCH	C01	into
	ds_equipe_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if	length(ds_retorno_w || ds_equipe_w) < 2001 then
	ds_retorno_w := substr(ds_retorno_w || ds_equipe_w || ', ',1,2000);

	end if;
END LOOP;
CLOSE C01;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
