CREATE OR REPLACE FUNCTION obter_equip_exame_amostra (nr_seq_exame_p bigint, ds_sigla_p text, nr_amostra_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(20);
nr_seq_exame_w	exame_laboratorio.nr_seq_exame%type;


BEGIN

select	max(nr_seq_exame)
into STRICT	nr_seq_exame_w
from	exame_laboratorio
where	nr_seq_superior = nr_seq_exame_p
and	nr_ordem_amostra = nr_amostra_p;


if (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then
	select	max(l.cd_exame_equip)
	into STRICT	ds_retorno_w
	from	lab_exame_equip l,
		equipamento_lab e
	where	l.nr_seq_exame 		= nr_seq_exame_w
	and	l.cd_equipamento 	= e.cd_equipamento
	and	e.ds_sigla		= ds_sigla_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
