CREATE OR REPLACE FUNCTION obter_erros_atualizacao_versao (nr_seq_atualizacao_w bigint) RETURNS varchar AS $body$
DECLARE


ds_tipo_w			varchar(100);
ds_comando_w		varchar(254);
ds_erro_w			varchar(254);
nm_usuario_w		varchar(15);
ds_cabecalho_w		varchar(500);
ds_retorno_w		varchar(4000);

C01 CURSOR FOR
	SELECT  CASE WHEN l.ie_evento='105' THEN obter_desc_expressao(320942) WHEN l.ie_evento='190' THEN obter_desc_expressao(302555) END  ds_tipo,
		substr(e.ds_comando,1,254) ds_comando,
		substr(e.ds_erro,1,254) ds_erro,
		e.nm_usuario
	from    log_atualizacao l,
		log_atualizacao_erro e
	where   e.nr_seq_log = l.nr_sequencia
	and	l.ie_evento  in ('105','190')
	and	l.nr_seq_atualizacao =  nr_seq_atualizacao_w
	order by ds_tipo, nm_usuario, ds_comando;


BEGIN

ds_retorno_w := '';

open C01;
loop
fetch C01 into
	ds_tipo_w,
	ds_comando_w,
	ds_erro_w,
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		if (length(ds_retorno_w || ds_tipo_w || chr(9) || ds_comando_w || chr(9) || ds_erro_w || chr(9) || nm_usuario_w || chr(13) || chr(10)) < 3800) then

			ds_retorno_w := ds_retorno_w || ds_tipo_w || chr(9) || ds_comando_w || chr(9) || ds_erro_w || chr(9) || nm_usuario_w || chr(13) || chr(10);

		end if;
	end;
end loop;
close C01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	begin
	select	obter_desc_expressao(648223)/*'Versão: '*/
 || cd_versao_atual || chr(9) || obter_desc_expressao(285513)/*'Comando'*/ || chr(9) || 'Erro' || chr(9) || obter_desc_expressao(314533)/*'Usuário'*/
	into STRICT	ds_cabecalho_w
	from	atualizacao_versao
	where	nr_sequencia = nr_seq_atualizacao_w;

	ds_retorno_w := ds_cabecalho_w || chr(13) || chr(10) || ds_retorno_w;
	end;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
