CREATE OR REPLACE FUNCTION obter_escala_audit (qt_pontos_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dependencia_w		varchar(40);


BEGIN

if (qt_pontos_p > 19) then
	ds_dependencia_w 	:= wheb_mensagem_pck.get_texto(308924); -- Provável dependência
elsif (qt_pontos_p > 15) then
	ds_dependencia_w 	:= wheb_mensagem_pck.get_texto(308925); -- Uso nocivo ou consumo de alto risco
elsif (qt_pontos_p > 7) then
	ds_dependencia_w 	:= wheb_mensagem_pck.get_texto(308926); -- Consumo de risco
else
	ds_dependencia_w 	:= wheb_mensagem_pck.get_texto(308927); -- Consumo de baixo risco ou abstêmios
end if;

return ds_dependencia_w;

end;
$body$
LANGUAGE PLPGSQL
