SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_escala_bibliografia ( ie_escala_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_Escala_Bibliografia_w      varchar(4000) := '';
  ds_Escala_Bibliografia_pais_w varchar(4000);
  is_Exists_w                   varchar(1) := 'N';
  ds_locale_w		escala_doc_locale.cd_pais%type;

BEGIN

ds_locale_w := coalesce(coalesce(pkg_i18n.get_user_locale,philips_param_pck.get_ds_locale),'pt_BR');

  IF (ie_escala_p IS NOT NULL AND ie_escala_p::text <> '') THEN
    BEGIN
      SELECT coalesce(SUBSTR(obter_desc_expressao(ed.CD_EXP_INFORMACAO),1,4000),'')
      INTO STRICT ds_Escala_Bibliografia_w
      FROM escala_documentacao ed
      WHERE ed.IE_ESCALA =ie_escala_p;
      SELECT CASE WHEN  COUNT(1)=0 THEN 'N'  ELSE 'S' END
      INTO STRICT is_Exists_w
      FROM ESCALA_DOC_LOCALE edl
      INNER JOIN escala_documentacao ed
      ON edl.NR_SEQ_ESCALA_DOC = ed.nr_sequencia
      WHERE ed.IE_ESCALA       =ie_escala_p
      AND edl.cd_pais          = ds_locale_w;
      IF ( is_Exists_w         ='S') THEN
        SELECT SUBSTR(obter_desc_expressao(MAX(edl.CD_EXP_REF_BIBLI)),1,4000)
        INTO STRICT ds_Escala_Bibliografia_pais_w
        FROM ESCALA_DOC_LOCALE edl
        INNER JOIN escala_documentacao ed
        ON edl.NR_SEQ_ESCALA_DOC = ed.nr_sequencia
        WHERE ed.IE_ESCALA       =ie_escala_p
        AND edl.cd_pais          = ds_locale_w;
      END IF;
    END;
  END IF;
IF (ds_Escala_Bibliografia_pais_w IS NOT NULL AND ds_Escala_Bibliografia_pais_w::text <> '') THEN
  ds_Escala_Bibliografia_w        := ds_Escala_Bibliografia_pais_w;
END IF;
RETURN ds_Escala_Bibliografia_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
