CREATE OR REPLACE FUNCTION obter_escala_dor_atend (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_escala_dor_w		bigint;

BEGIN

select	a.qt_escala_dor
into STRICT	qt_escala_dor_w
from	atendimento_sinal_vital a
where	a.nr_sequencia = (	SELECT	coalesce(MAX(NR_SEQUENCIA),-1)
				FROM	ATENDIMENTO_SINAL_VITAL E
				WHERE	(CD_ESCALA_DOR IS NOT NULL AND CD_ESCALA_DOR::text <> '')
				AND	IE_SITUACAO = 'A'
				AND	NR_ATENDIMENTO = NR_ATENDIMENTO_P
				AND	coalesce(IE_RN,'N')	= 'N'
				AND	(DT_LIBERACAO IS NOT NULL AND DT_LIBERACAO::text <> '')
				AND	coalesce(DT_INATIVACAO::text, '') = '');


return	qt_escala_dor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
