CREATE OR REPLACE FUNCTION obter_escala_dor_perfil ( cd_perfil_p bigint, cd_escala_dor_p text, nr_atendimento_p bigint default null, cd_paciente_p text default null) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'S';
qt_reg_escala_w			integer	:= 0;
cd_setor_atendimento_w	integer;
qt_idade_w				double precision;
nr_atendimento_w		bigint;
cd_estabelecimento_w	bigint;	


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	count(*)
into STRICT	qt_reg_escala_w
from	escala_dor_perfil
where	coalesce(cd_perfil,cd_perfil_p) = cd_perfil_p
and		coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;

nr_atendimento_w := coalesce(nr_atendimento_p,0);

begin
select	coalesce(obter_setor_atendimento(nr_atendimento_w),0)
into STRICT	cd_setor_atendimento_w
;
exception
	when others then
	null;
end;
if (nr_atendimento_w > 0) then
	select	max(obter_idade(b.dt_nascimento,clock_timestamp(),'DIA'))
	into STRICT	qt_idade_w
	from	pessoa_fisica b,
		atendimento_paciente a
	where	a.nr_atendimento	= nr_atendimento_w
	and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica;
elsif (cd_paciente_p IS NOT NULL AND cd_paciente_p::text <> '') then
	select	max(obter_idade(b.dt_nascimento,clock_timestamp(),'DIA'))
	into STRICT	qt_idade_w
	from	pessoa_fisica b
	where	b.cd_pessoa_fisica	= cd_paciente_p;
end if;

begin

if (qt_reg_escala_w	> 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	escala_dor_perfil
	where	coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
	and		coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	coalesce(cd_escala_dor,cd_escala_dor_p)	= cd_escala_dor_p
	and	((nr_atendimento_w = 0) or (coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w))
	and	((qt_idade_w =0) or qt_idade_w between Obter_idade_sinal_vital(nr_sequencia,'MIN') and Obter_idade_sinal_vital(nr_sequencia,'MAX'));
end if;

exception
	when others then
	ie_retorno_w := 'S';
end;

return	ie_retorno_w;

end;
