CREATE OR REPLACE FUNCTION obter_escala_dor_sinal_vital ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

vl_retorno_w	varchar(255);

BEGIN
if (ie_opcao_p = 'MIN') then
	select (substr(obter_valor_dominio(1298, cd_escala_dor),1,60) || ' / ' || qt_escala_dor)
	into STRICT	vl_retorno_w
	from	atendimento_sinal_vital
	where	nr_sequencia = (SELECT  coalesce(min(nr_sequencia),0)
		from	atendimento_sinal_vital
		where	(qt_escala_dor IS NOT NULL AND qt_escala_dor::text <> '')
		and	nr_atendimento  = nr_atendimento_p
		and	coalesce(ie_situacao,'A') = 'A'  );
elsif (ie_opcao_p = 'MAX') then
	select (substr(obter_valor_dominio(1298, cd_escala_dor),1,60) || ' / ' || qt_escala_dor)
	into STRICT	vl_retorno_w
	from	atendimento_sinal_vital
	where	nr_sequencia = (SELECT  coalesce(max(nr_sequencia),0)
		from	atendimento_sinal_vital
		where	(qt_escala_dor IS NOT NULL AND qt_escala_dor::text <> '')
		and	nr_atendimento  = nr_atendimento_p
		and	coalesce(ie_situacao,'A') = 'A' );
end	if;
return	vl_retorno_w;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
