CREATE OR REPLACE FUNCTION obter_escala_exame (nr_seq_exame_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_escala_w	bigint;

BEGIN
	select nr_seq_escala
  	into STRICT nr_seq_escala_w
		from (	SELECT nr_seq_escala, ie_prioridade
			from 	LAB_LOINC_ESCALA_EXAME
			where nr_seq_exame = nr_seq_exame_p
			and	coalesce(ie_situacao,'A') = 'A'
			order by ie_prioridade) alias1 LIMIT 1;

  return	nr_seq_escala_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
