CREATE OR REPLACE FUNCTION obter_escala_ipss (qt_pontos_p bigint) RETURNS varchar AS $body$
DECLARE


ds_referencia_w			varchar(50);


BEGIN

if (qt_pontos_p > 19) then
	ds_referencia_w 	:= Wheb_mensagem_pck.get_texto(309517); --'Severa';
elsif (qt_pontos_p > 7) then
	ds_referencia_w 	:= Wheb_mensagem_pck.get_texto(309518); --'Moderada';
else
	ds_referencia_w		:= Wheb_mensagem_pck.get_texto(309519); --'Leve';
end if;

return	ds_referencia_w;

end;
