CREATE OR REPLACE FUNCTION obter_escala_morse_paciente (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_descricao_w varchar(30) :=  null;
qt_pont_mews_w integer;
dt_registro_w varchar(30);


BEGIN

select	max(QT_PONTUACAO), substr(to_char(max(a.dt_avaliacao), 'dd/mm/yyyy'),1, 10)
into STRICT	qt_pont_mews_w, dt_registro_w
from	ESCALA_MORSE a
where	nr_sequencia	= (	SELECT	max(x.nr_sequencia)
				from	ESCALA_MORSE x
				where	x.nr_atendimento	= nr_atendimento_p
				and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
				and	coalesce(x.dt_inativacao::text, '') = '');

if (qt_pont_mews_w IS NOT NULL AND qt_pont_mews_w::text <> '') then
ds_descricao_w := SubStr(Obter_desc_escala_morse(qt_pont_mews_w), 1, 13) || ' - ' || dt_registro_w;
end if;

return ds_descricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
