CREATE OR REPLACE FUNCTION obter_escala (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_escala_w varchar(60);

BEGIN

ds_escala_w := '';

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	coalesce(max(ds_escala),'')
	into STRICT	ds_escala_w
	from	escala
	where	nr_sequencia = nr_sequencia_p;
end if;

return ds_escala_w;
end;
$body$
