CREATE OR REPLACE FUNCTION obter_especialidades_medico_cd ( cd_medico_p text) RETURNS varchar AS $body$
DECLARE


cd_especialidade_w		integer		:= null;
ds_especialidade_ww		varchar(4000);

/*
Vai retornar todos os codigos de especialidade do medico separadas por ( , ) virgula.
*/
c01 CURSOR FOR
SELECT	a.cd_especialidade
from	   especialidade_medica b,
         medico_especialidade a
where	   a.cd_especialidade	= b.cd_especialidade
and	   a.cd_pessoa_fisica	= cd_medico_p
order	by a.nr_seq_prioridade desc;


BEGIN

open c01;
loop
fetch c01 into
      cd_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
     ds_especialidade_ww	:= ds_especialidade_ww || ',' || cd_especialidade_w;
end loop;
close c01;

if (substr(ds_especialidade_ww,1,1) = ',') then
	ds_especialidade_ww		:= substr(ds_especialidade_ww,2,length(ds_especialidade_ww));
end if;

return ds_especialidade_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
