CREATE OR REPLACE FUNCTION obter_especialidade_cross (cd_medico_p text) RETURNS varchar AS $body$
DECLARE

 
cd_cross_w varchar(4);
ds_retorno_w varchar(4) := '';

c01 CURSOR FOR 
SELECT	b.cd_cross 
from	especialidade_medica b, 
		medico_especialidade a 
where	a.cd_especialidade	= b.cd_especialidade 
and		a.cd_pessoa_fisica	= cd_medico_p 
order	by a.nr_seq_prioridade desc;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	cd_cross_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_retorno_w	:= cd_cross_w;
end loop;
close c01;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
