CREATE OR REPLACE FUNCTION obter_especialidade_medico_cod ( cd_medico_p text) RETURNS varchar AS $body$
DECLARE

 
cd_especialidade_w		integer		:= null;
ds_especialidade_w		varchar(255)	:= '';
ds_especialidade_ww		varchar(4000);

c01 CURSOR FOR 
SELECT	a.cd_especialidade 
from	especialidade_medica b, 
	medico_especialidade a 
where	a.cd_especialidade	= b.cd_especialidade 
and	a.cd_pessoa_fisica	= cd_medico_p 
order	by a.nr_seq_prioridade desc;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	cd_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (coalesce(ds_especialidade_ww::text, '') = '') then 
		ds_especialidade_ww	:= cd_especialidade_w;
	else	 
		ds_especialidade_ww	:= ds_especialidade_ww || ',' || cd_especialidade_w;
	end if;	
end loop;
close c01;
 
return ds_especialidade_ww;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
