CREATE OR REPLACE FUNCTION obter_especialidade_medico ( cd_medico_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_especialidade_w		integer		:= null;
ds_especialidade_w		varchar(255)	:= '';
ds_especialidade_ww		varchar(4000);

/*
D	- decrição da ultima especialidade cadastrada
DV	- descrição de todas especialidades separadas por ( , ) vÍrgula.
*/
c01 CURSOR FOR
SELECT	a.cd_especialidade,
	b.ds_especialidade
from	especialidade_medica b,
	medico_especialidade a
where	a.cd_especialidade	= b.cd_especialidade
and	a.cd_pessoa_fisica	= cd_medico_p
order	by a.nr_seq_prioridade desc;


BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		cd_especialidade_w,
		ds_especialidade_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (substr(ie_opcao_p,1,2) = 'D') then
			ds_especialidade_ww	:= ds_especialidade_w;
		elsif (substr(ie_opcao_p,1,2) = 'DV') then
			ds_especialidade_ww	:= ds_especialidade_ww || ', ' || ds_especialidade_w;
		else
			ds_especialidade_ww	:= cd_especialidade_w;
		end if;
	end loop;
	close c01;
	if (substr(ds_especialidade_ww,1,2) = ', ') then
		ds_especialidade_ww		:= substr(ds_especialidade_ww,2,length(ds_especialidade_ww));
	end if;
end if;

return ds_especialidade_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
